import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo = 'Registro de Usuarios';
  mensaje ="";
  registrado=false;
  nombre:string="";
  apellido:string="";

  registrarUsuario(){

    this.registrado=true;

    this.mensaje="usuario registrado con éxito";
  }


}
<h1 style="text-align: center;">{{titulo}}</h1>

<form style="text-align: center;">

  Nombre: <input type="text" name="nombre" [(ngModel)]="nombre"><br><br>

  Apellido: <input type="text" name="apellido" [(ngModel)]="apellido"><br><br>

  <input type="submit" value="Registrar" (click)="registrarUsuario()">

</form>

<br>

<p style="text-align: center;" *ngIf="registrado">{{mensaje}} con el nombre de {{nombre}} {{apellido}}</p>
