import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo = 'Registro de Usuarios';
  mensaje ="";
  registrado=false;
  nombre:string="";
  apellido:string="";
  cargo:string="";
  entradas: any[];

  constructor(){

    this.entradas=[

      {titulo:"Python cada día más presente"},
      {titulo:"Java presente desde hace más de 20 años"},
      {titulo:"JavaScript cada vez más funcional"},
      {titulo:"Kotlin potencia para tus apps"},
      {titulo:"¿Dónde quedó pascal?"},
    ]
  }


  registrarUsuario(){

    this.registrado=true;

    this.mensaje="usuario registrado con éxito";
  }


}
s<h1 style="text-align: center;">{{titulo}}</h1>

<form style="text-align: center;">

  Nombre: <input type="text" name="nombre" [(ngModel)]="nombre"><br><br>

  Apellido: <input type="text" name="apellido" [(ngModel)]="apellido"><br><br>

  Cargo: <input type="text" name="cargo" [(ngModel)]="cargo"><br><br>

  <input type="submit" value="Registrar" (click)="registrarUsuario()">

</form>

<br>

<p style="text-align: center;" *ngIf="registrado; else sinRegistrar">{{mensaje}} con el nombre de {{nombre}} {{apellido}}
   y tiene el cargo de <span [ngStyle]="{color:'red'}"> {{cargo}} </span></p>

<ng-template #sinRegistrar>
  <p style="text-align: center;">Nadie registrado</p>
</ng-template>

<br><br>
<ul style="text-align: center; list-style: inside;">
  <li *ngFor="let entrada of entradas; let i=index"><span style="font-size: larger; font-weight: bold;">{{entrada.titulo}}</span>
  . Entrada {{i+1}}</li>
</ul>
